import React, { useEffect, useState } from "react";
import axios from "axios";
import os from "os";

function App() {
    const [data, setData] = useState(null);
    const [cpuInfo, setCpuInfo] = useState('');
    const [gpuInfo, setGpuInfo] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    // const [confirmPassword, setConfirmPassword] = useState('');

    const os = require('os');


    useEffect(() => {
        // CPU 정보 가져오기
        const cpu = os.cpus();
        console.log(cpu);
        // const cpu = navigator.hardwareConcurrency;
        // setCpuInfo(`CPU 모델명: ${cpu}`);

        // WebGL을 사용하여 GPU 정보 가져오기
        const canvas = document.createElement('canvas');
        const gl = canvas.getContext('webgl') || canvas.getContext('experimental-webgl');
        if (gl) {
            const info = gl.getExtension('WEBGL_debug_renderer_info');
            if (info) {
                const gpu = gl.getParameter(info.UNMASKED_RENDERER_WEBGL);
                setGpuInfo(`GPU 모델명: ${gpu}`);
            } else {
                setGpuInfo('GPU 정보를 가져올 수 없습니다.');
            }
        } else {
            setGpuInfo('WebGL을 지원하지 않는 브라우저입니다.');
        }
    }, []);

    const handleClick = async () => {
        try {
            const response = await axios.get("http://localhost:12000");
            console.log("받은 데이터:", response.data);
            setData(response.data);
        } catch (error) {
            console.error(error);
            // TODO: 오류 처리 로직을 추가
        }
    };


    const handleInputChange = event => {
        if(event.target.name === "email"){
            setEmail(event.target.value);
        }
        else if(event.target.name === "password"){
            setPassword(event.target.value);
        }
        // else if(event.target.name === "confirmPassword"){
        //     setConfirmPassword(event.target.value);
        // }
    }

    const handleSubmit = event => {
            event.preventDefault();
            const userData = {
                email: email,
                password: password
                // confirmPassword: confirmPassword
            };
            console.log("first clear");
            axios.post('http://localhost:12000/api/signup', userData)
                .then(response => {
                    console.log(response);
                })
                .catch(error => {
                    console.log(error);
                });
            console.log("second clear");
        }


    return (
        <div>
            <button onClick={handleClick}>Send Request to Spring Boot Server</button>
            {data && (
                <ul>
                    {data.map((item) => (
                        <li key={item.id}>{item.name}</li>
                    ))}
                </ul>
            )}
            {cpuInfo !== null && <p>CPU 정보: {cpuInfo}</p>}
            {gpuInfo !== null && <p>GPU 정보: {gpuInfo}</p>}
            <form onSubmit={handleSubmit}>
            <div>
                <label htmlFor="email">Email:</label>
                <input type="email" id="email" name="email" onChange={handleInputChange} />
            </div>
            <div>
                <label htmlFor="password">Password:</label>
                <input type="password" id="password" name="password" onChange={handleInputChange} />
            </div>
            {/*<div>*/}
            {/*    <label htmlFor="confirmPassword">Confirm Password:</label>*/}
            {/*    <input type="password" id="confirmPassword" name="confirmPassword" onChange={handleInputChange} />*/}
            {/*</div>*/}
            <button type="submit">Submit</button>
            </form>
        </div>

    );
}

export default App;
